import React, { useState, useEffect } from "react";
import axios from "axios";

const getEmoji = (expression) => {
  const length = expression.replace(/[()]/g, "").trim().length;
  const depth = (expression.match(/[()]/g) || []).length;
  const complexity = length + Math.floor(depth / 2); // adjusted

  if (complexity < 5) return "ðŸ˜¡";
  if (complexity < 10) return "ðŸ˜ ";
  if (complexity < 15) return "ðŸ™‚";
  if (complexity < 20) return "ðŸ˜„";
  return "ðŸ˜ƒ";
};

const Calculator = () => {
  const [expression, setExpression] = useState("");
  const [result, setResult] = useState("");
  const [lastExpression, setLastExpression] = useState("");
  const [history, setHistory] = useState([]);

  const fetchHistory = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/history");
      setHistory(res.data);
    } catch (err) {
      console.error("Error fetching history", err);
    }
  };

  useEffect(() => {
    fetchHistory();
  }, []);

  const handleCalculate = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/calculate", {
        expression,
      });
      setResult(res.data.result);
      setLastExpression(expression);
      setExpression("");
      fetchHistory();
    } catch (err) {
      console.error(err);
      setResult("Error");
    }
  };

  const handleClearHistory = async () => {
    try {
      await axios.delete("http://localhost:5000/api/history");
      setHistory([]);
    } catch (err) {
      console.error("Error clearing history", err);
    }
  };

  return (
    <div className="min-h-screen bg-background flex flex-col items-center p-6 text-gray-800">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-xl">
        <h2 className="text-3xl font-bold mb-6 text-primary text-center">
          ðŸ§® Simply Calculate
        </h2>

        <input
          type="text"
          value={expression}
          onChange={(e) => setExpression(e.target.value)}
          placeholder="Enter expression (e.g. 5 + 5)"
          className="w-full p-3 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-brightPink"
        />

        <button
          onClick={handleCalculate}
          className="mt-4 w-full bg-brightPink text-white py-2 px-4 rounded hover:bg-darkPink transition duration-300"
        >
          Calculate
        </button>

        <h3 className="text-xl mt-6 text-center font-semibold">
          Result: {result} {result && getEmoji(lastExpression)}
        </h3>
      </div>

      <div className="w-full max-w-xl mt-10 bg-white shadow-lg rounded-lg p-6">
        <h3 className="text-2xl font-semibold text-primary mb-4">
          ðŸ•’ Calculation History
        </h3>

        {history.length === 0 ? (
          <p className="text-gray-500">No calculations yet.</p>
        ) : (
          <>
            <ul className="space-y-4">
              {history.map((item) => (
                <li
                  key={item._id}
                  className="bg-lightPink p-3 rounded shadow-sm"
                >
                  <div>
                    <strong className="text-darkPink">{item.expression}</strong>{" "}
                    = {item.result} {getEmoji(item.expression)}
                  </div>
                  <small className="text-gray-600 block">
                    {new Date(item.createdAt).toLocaleString()}
                  </small>
                </li>
              ))}
            </ul>
            <button
              onClick={handleClearHistory}
              className="mt-6 w-full bg-red-600 text-white py-2 px-4 rounded hover:bg-red-700 transition duration-300"
            >
              Clear History
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Calculator;
