import React, { useState, useEffect } from "react";
import axios from "axios";
import "../index.css"; // Optional if you move custom styles here

const getEmoji = (expression) => {
  const length = expression.replace(/[()]/g, "").trim().length;
  const depth = (expression.match(/[()]/g) || []).length;
  const complexity = length + Math.floor(depth / 2);
  if (complexity < 5) return "ðŸ˜¡";
  if (complexity < 10) return "ðŸ˜ ";
  if (complexity < 15) return "ðŸ™‚";
  if (complexity < 20) return "ðŸ˜„";
  return "ðŸ˜ƒ";
};

const Calculator = () => {
  const [expression, setExpression] = useState("");
  const [result, setResult] = useState("");
  const [lastExpression, setLastExpression] = useState("");
  const [history, setHistory] = useState([]);

  const fetchHistory = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/history");
      setHistory(res.data);
    } catch (err) {
      console.error("Error fetching history", err);
    }
  };

  useEffect(() => {
    fetchHistory();
  }, []);

  const handleCalculate = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/calculate", {
        expression,
      });
      setResult(res.data.result);
      setLastExpression(expression);
      setExpression("");
      fetchHistory();
    } catch (err) {
      console.error(err);
      setResult("Error");
    }
  };

  const handleClearHistory = async () => {
    try {
      await axios.delete("http://localhost:5000/api/history");
      setHistory([]);
    } catch (err) {
      console.error("Error clearing history", err);
    }
  };

  return (
    <div className="main-container">
      <div className="calculator-box">
        <h2 className="calculator-heading">ðŸ§® Simply Calculate</h2>

        <input
          type="text"
          value={expression}
          onChange={(e) => setExpression(e.target.value)}
          placeholder="Enter expression (e.g. 5 + 5)"
          className="input-field"
        />

        <button onClick={handleCalculate} className="calculate-btn">
          Calculate
        </button>

        <h3 className="result">
          Result: {result} {result && getEmoji(lastExpression)}
        </h3>
      </div>

      <div className="history-box">
        <h3 className="history-title">ðŸ•’ Calculation History</h3>

        {history.length === 0 ? (
          <p className="no-history-text">No calculations yet.</p>
        ) : (
          <>
            <ul className="history-list">
              {history.map((item) => (
                <li key={item._id} className="history-item">
                  <div>
                    <strong className="expression">{item.expression}</strong> ={" "}
                    {item.result} {getEmoji(item.expression)}
                  </div>
                  <small className="timestamp">
                    {new Date(item.createdAt).toLocaleString()}
                  </small>
                </li>
              ))}
            </ul>
            <button onClick={handleClearHistory} className="clear-btn">
              Clear History
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Calculator;
